svg.script:

// init vars
var rect1 = document.getElementById("rect1");
var rect2 = document.getElementById("rect2");
var text1 = document.getElementById("text1");
var text2 = document.getElementById("text2");


var textWidth1 = text1.offsetWidth;
var textPadding1 = 10;
var rectWidth1 = textWidth1 + 10 + 20; // set rect 1 width

// start 1 chopping it up
rect1.setAttribute("width", rectWidth1);
text1.setAttribute("transform", "matrix(1 0 0 1 "+textPadding1+" 21)")


var rectLocation2 = rectWidth1; // put the rect right next to the other
var textWidth2 = text2.offsetWidth;
var textPadding2 = rectLocation2 + 17;
var rectWidth2 = textWidth2 + 10 + 20;

// start 2 chopping it up
rect2.setAttribute("x", rectLocation2);
rect2.setAttribute("width", rectWidth2);
text2.setAttribute("transform", "matrix(1 0 0 1 " + textPadding2 + " 21)")
